clear;                          % Effacement du Workspaceclose all;                      % Fermeture de toutes les figuresclc;                            % Effacement de la fenêtre de commandeformat long;epsilon_tab = [5,1,0.1,0.001,0.0001,0.00001,0.000001,0.000000001,0.0000000001,0.00000000001];f = @(x)x.^2-3*x-2;function [c,n] = dichoto(a,b,epsilon,f)  g=min(a,b); d=max(a,b);  n=0;  while(d-g>epsilon)    n+=1;    c = (g+d)/2;    if (f(g)*f(c)<0)      d=c;    else      g=c;    endif  endwhileendfor i=1:10    [result(i),iter(i)] = dichoto(0,10,epsilon_tab(i),f)  erreur(i) = (abs(result(i)-3.561553)/3.561553)*100;endsubplot(2,1,1)semilogx(epsilon_tab,iter);title("Nombre d'itération en fonction de epsilon")xlabel("Epsilon")ylabel("Nombre d'itérations")subplot(2,1,2)semilogx(epsilon_tab,erreur)title("Erreur sur la solution en fonction de epsilon")xlabel("Epsilon")ylabel("Erreur en %")